graphql.query:
query Node($filter: ProductOfferFilterInput) {
  productOffers(filter: $filter) {
    edges {
      node {
        customer {
          msisdn
          id
        }
        id
        quantity
        specification {
          slug
          version
          general {
            name
            description
            shortDescription
          }
        }
        tariffComponents {
          prices {
            type
            amount {
              amount
              currency
            }
          }
        }
        priceList {
          firstPrice {
            amount {
              base
              result
              currency
            }
          }
          basePrice {
            amount {
              base
              result
              currency
            }
          }
          connectionPrice {
            amount {
              base
              result
              currency
            }
          }
          regularPrice {
            chargingInterval {
              duration
              unit
            }
          }
        }
        touchPointView {
          general {
            name
            description
            shortDescription
          }
                    parameters {
            customerEditable {
              alias
              availableValues {
                ... on ParameterListValue {
                  unit
                  valueType
                  values
                }
                ... on ParameterRangeValue {
                  unit
                  valueFrom
                  valueStep
                  valueTo
                  valueType
                }
                ... on ParameterSingleValue {
                  unit
                  value
                  valueType
                }
              }
              defaultValue {
                unit
                value
                valueType
              }
              itemAlias
              marketingName
              value {
                unit
                value
                valueType
              }
            }
            predefined {
              alias
              defaultValue {
                unit
                value
                valueType
              }
              itemAlias
              marketingName
              value {
                unit
                value
                valueType
              }
            }
          }
          items {
            customerEditable {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
            predefined {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias

                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
          }
        }
      }
    }
  }
}

