      profile {
        activationBaseUrl
      }
    }
    type
  }
  general {
    ...MarketingInfo
  }
  externalId
  parameters {
    alias
    serviceSpecParamCode
    name
    value {
      value
      valueType
      unit
    }
  }
}

fragment OfferResourcesConfiguration on OfferResourcesConfiguration {
  alias
  specification {
    id
    version
  }
  general {
    ...MarketingInfo
  }
}

fragment OfferPrice on OfferPrice {
  type
  componentId
  action {
    slug
    description
  }
  resultPrice {
    ...Money
  }
  firstPrice {
    ...Money
  }
  basePrice {
    ...Money
  }
  modifiers {
    discounts {
      ... on PercentDiscount {
        percent
        duration {
          ...DateInterval
        }
        reason {
          ...ModifierReason
        }
      }
      ... on AmountDiscount {
        amount {
          ...Money
        }
        duration {
          ...DateInterval
        }
        reason {
          ...ModifierReason
        }
      }
      ... on AbsoluteDiscount {
        newPrice {
          ...Money
        }
        duration {
          ...DateInterval
        }
        reason {
          ...ModifierReason
        }
      }
    }
    paymentDateShift {
      duration {
        ...DateInterval
      }
      reason {
        ...ModifierReason
      }
    }
  }
  interval {
    ...DateInterval
  }
  estimatedStartDate
  isEnabled
}

fragment OfferTariffComponent on OfferTariffComponent {
  id
  prices {
    type
    amount {
      amount
      currency
    }
  }
  isEnabled
}

fragment PriceList on PriceList {
  connectionPrice {
    ...OneTimePriceSummary
  }
  firstPrice {
    ...OneTimePriceSummary
  }
  regularPrice {
    ...RegularPriceSummary
  }
}

fragment OneTimePriceSummary on OneTimePriceSummary {
  amount {
    ...TouchPointAmount
  }
}

fragment RegularPriceSummary on RegularPriceSummary {
  amount {
    ...TouchPointAmount
  }
}

fragment TouchPointAmount on TouchPointAmount {
  base
  result
  currency
}

mutation UpdateProductOrderContext($input: UpdateProductOrderContextInput!) {
  updateProductOrderContext(input: $input) {
    state
    errors {
      message
      code
    }
    order {
      id
      customer {
        msisdn
        billingAccountNumber
      }
      recipient {
        email
        fullName
        phone
      }
      totalPrice {
        cashback {
          amount
          currency
        }
        amount {
          amount
          currency
        }
      }
      contactPerson {
        fullName
        phone
        email
      }
    }
    prepaymentRequired
  }
}

