query Node($filter: ProductOfferFilterInput) {
  productOffers(filter: $filter) {
    edges {
      node {
        customer {
          msisdn
          id
        }
         offerProvider {
          source
          externalProductId
        }
        id
        quantity
        specification {
          slug
          version
          general {
            name
            description
            shortDescription
          }
        }
        tariffComponents {
          prices {
            type
            amount {
              amount
              currency
            }
          }
        }
        priceList {
          firstPrice {
            amount {
              base
              result
              currency
            }
          }
          basePrice {
            amount {
              base
              result
              currency
            }
          }
          connectionPrice {
            amount {
              base
              result
              currency
            }
          }
          regularPrice {
            chargingInterval {
              duration
              unit
            }
          }
        }
        touchPointView {
          general {
            name
            description
            shortDescription
          }
          items {
            customerEditable {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
            predefined {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias
                general {
                  shortDescription
                  description
                  name
                }
                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
          }
        }
       
      }
    }
  }
}

mutation CreateCheckoutFromOffers($input: CreateCheckoutFromOffersInput!) {
  createCheckoutFromOffers(input: $input) {
    isSuccess
    error {
      code
      message
    }
    checkout {
      checkoutItems {
        channel {
          slug
        }
        id
        name
        price {


          lineLoyaltyPrice {
            amount
            currency
          }
          lineResultPrice {
            amount
            currency
          }

          loyalty {
            cashback {
              currency
              payment {
                availableForPay
                reason {
                  code
                  description
                }
              }
            }
          }
        }
        quantity
      }
      checkoutItemsCount
      checkoutItemsTotalQuantity
      price {
        discountTotal {
          amount
          currency
        }
        grandTotal {
          amount
          currency
        }
        subTotal {
          amount
          currency
        }
      }
      updatedAt
      id
      paymentTools {
        nodes {
          id
          type
          title
          isDefault
          optionalAttributes {
            category
            key
            name
            value
          }
        }
        totalCount
      }
    }
  }
}

mutation CompleteCheckout($input: CompleteCheckoutInput!) {
  completeCheckout(input: $input) {
    isSuccess
    error {
      code
      message
    }
    order {
      id
      totalPrice {
        amount {
          amount
          currency
        }
        cashback {
          amount
          currency
        }
      }
      state
      channel {
        slug
      }
      items {
        product {
          productConfiguration {
            services {
              externalId
            }
          }
          productSpecification {
            id
          }
        }

      }
    }
    paymentSession {
      paymentSessionId
      paymentTokenId
    }
  }
}

