query Node($filter: ProductOfferFilterInput) {
  productOffers(filter: $filter) {
    edges {
      node {
        customer {
          msisdn
          id
        }
        id
        quantity
        specification {
          slug
          version
          general {
            name
            description
            shortDescription
          }
        }
        tariffComponents {
          prices {
            type
            amount {
              amount
              currency
            }
          }
        }
        priceList {
          firstPrice {
            amount {
              base
              result
              currency
            }
          }
          basePrice {
            amount {
              base
              result
              currency
            }
          }
          connectionPrice {
            amount {
              base
              result
              currency
            }
          }
          regularPrice {
            chargingInterval {
              duration
              unit
            }
          }
        }
        touchPointView {
          general {
            name
            description
            shortDescription
          }
          items {
            customerEditable {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
            predefined {
              ... on ProductItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
              ... on ServiceItem {
                alias
                general {
                  shortDescription
                  description
                  name
                }
                isEnabled
              }
              ... on ResourceItem {
                alias
                general {
                  name
                  description
                  shortDescription
                }
                isEnabled
              }
            }
          }
        }
      }
    }
  }
}

mutation AddToCartAndCheckout ($input: AddToCartAndCheckoutInput!)
{
    addToCartAndCheckout(input: $input)

    {
        isSuccess
        error {
            code
            message
        }
        order {
            id
        }
        paymentSession {
            paymentSessionId
            paymentTokenId
            paymentSessionReturnReason
        }
    }
}

