}

mutation DoChargeSubscription($input: ChargeSubscriptionInput!) {
  doChargeSubscription(input: $input) {
    ... on OperationState {
      state
    }
    ... on Error {
      code
      message
    }
  }
}

mutation TerminateProduct($input: ProductOperationInput!) {
  terminateProduct(input: $input) {
    state
    errors {
      code
      message
    }
  }
}

